import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";
import Card from "./components/cards";
import $ from "jquery";

function App() {
  const [values, setValues] = useState();
  const [listGames, setListGames] = useState();

  useEffect(() => {
    axios.get("http://localhost:3001/getCards").then((response) => {
      setListGames(response.data);
    });
  });

  const handleChangeValues = (value) => {
    setValues((prevValue) => ({
      ...prevValue,
      [value.target.name]: value.target.value,
    }));
  };

  const handleClickButton = () => {
    console.log(values);
    axios
      .post("http://localhost:3001/register", {
        name: values.name,
        cost: values.cost,
        category: values.category,
      })
      .then((response) => {
        console.log(response);
      });
  };

  function validateMoney(input) {
    const regex = /^\d+(\.\d{2})?$/;
    const valid = regex.test(input.value);
    input.setCustomValidity(
      valid ? "" : "Enter a valid amount with two decimal places."
    );
  }

  return (
    <div className="app-container">
      <div class="card register--container mx-auto">
        <div class="card-body">
          <h5 class="card-title text-center">Scrim Shop</h5>
          <div class="form-group">
            <input
              type="text"
              class="form-control register--input"
              placeholder="Nome"
              name="name"
              onChange={handleChangeValues}
            />
            <input
              type="number"
              min="0.00"
              max="10000.00"
              step="0.01"
              placeholder="0.00"
              required
              class="form-control register--input"
              name="cost"
              oninput={validateMoney(this)}
              onChange={handleChangeValues}
            />
            <input
              type="text"
              class="form-control register--input"
              placeholder="Category"
              name="category"
              onChange={handleChangeValues}
            />
            <button
              type="button"
              class="btn btn-primary register--button"
              onClick={() => {
                handleClickButton();
              }}
            >
              Primary
            </button>
          </div>
        </div>
      </div>
      <div className="card-group">
        {typeof listGames !== "undefined" &&
          listGames.map((value) => {
            return (
              <Card
                key={value.id}
                setListCard={setListGames}
                id={value.idgames}
                name={value.name}
                cost={value.cost}
                category={value.category}
              ></Card>
            );
          })}
      </div>
    </div>
  );
}

export default App;
